mapper.update:
update Song
set songName=#{songName}
where songId = #{songId}

mapper.delete:
delete
from SongCollection
where userId = #{userId}
  and songId = #{songId}

mapper.select:
select songId, songName, albumId, url, songCoverUrl
from Song
where songId = #{songId}

select songId, songName, albumId, url, songCoverUrl
from Song
where songName = #{songName}

select songId, songName, albumId, url, songCoverUrl
from Song
where albumId = #{albumId}

select songId, songName, albumId, url, songCoverUrl
from Song
where songName like concat(#{songName}, '%');

select a.songId, a.songName, a.albumId, a.url, a.songCoverUrl
from Song a,
     SongCollection b
where a.songId = b.songId
  and b.userId = #{userId}
